mapper.select:
SELECT t1.s_id, t1.s_parent_id, t1.s_name, t2.s_name AS s_parentName, t1.s_source_key, t1.s_type, t1.s_source_url, t1.s_level, t1.s_icon, t1.s_is_hide, t1.s_description, t1.s_create_time, t1.s_update_time FROM tb_resource t1 LEFT JOIN tb_resource t2 ON t1.s_parent_id = t2.s_id
and t1.s_name=#{name}
and t1.s_is_hide=${isHide}
and t1.s_type=${type}

SELECT t1.s_id, t1.s_parent_id, t1.s_name, t2.s_name AS s_parentName, t1.s_source_key, t1.s_type, t1.s_source_url, t1.s_level, t1.s_icon, t1.s_is_hide, t1.s_description, t1.s_create_time, t1.s_update_time, CASE WHEN t3.s_id IS NULL THEN FALSE ELSE TRUE END AS s_selected FROM tb_resource t1 LEFT JOIN tb_resource t2 ON t1.s_parent_id = t2.s_id LEFT JOIN tb_resources_role t3 on t1.s_id = t3.s_id and t3.r_id=${roleId}
and t1.s_is_hide=${isHide}
and t1.s_type=${type}

mapper.update:
update tb_resource set
s_parent_id = null, s_parent_id = ${parentId},
s_name = #{name}, s_source_key = #{sourceKey}, s_type = ${type}, s_source_url = #{sourceUrl}, s_icon = #{icon}, s_is_hide = ${isHide}, s_description = #{description} where s_id = ${id}
