mapper.update:
update tb_role

	
		r_name = #{name},
	
	
		r_key = #{key},
	
	
		r_status = #{status},
	
	
		r_description = #{description}
	

where r_id = ${id}

mapper.select:
SELECT count(*)
FROM tb_role_user
where r_id = ${roleId}

SELECT count(*)
FROM tb_resources_role
where r_id = ${roleId}
